import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Register = () => {
  const history = useHistory(); // Ensure this line is correct

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    description: '',
    password: '',
    profilePicture: null
  });

  const { name, email, description, password, profilePicture } = formData;

  const onChange = e => {
    if (e.target.name === 'profilePicture') {
      setFormData({ ...formData, profilePicture: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const onSubmit = async e => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append('name', name);
    formDataToSend.append('email', email);
    formDataToSend.append('description', description);
    formDataToSend.append('password', password);
    formDataToSend.append('profilePicture', profilePicture);

    try {
      const response = await axios.post('http://localhost:5000/register', formDataToSend);
      console.log('Registration successful:', response.data);
      // Redirect to login page after successful registration
      history.push('/login');
    } catch (error) {
      console.error('Registration error:', error);
      // Handle error, show an error message to the user
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={onSubmit}>
        {/* Your form inputs and submit button */}
      </form>
    </div>
  );
};

export default Register;
